import React from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { useDispatch } from "react-redux";
import { deleteContact } from "../../redux/action/phoneBookAction";
import styles from "./ContactList.module.css";
import "./ContactList.css";

const ContactList = ({ filterContacts }) => {
  const dispatch = useDispatch();
  return (
    <ul>
      <TransitionGroup component="ul" className="list">
        {filterContacts().map(
          (contact) => (
            console.log(contact.id),
            deleteContact(() => dispatch(deleteContact(contact.id))),
            (
              <CSSTransition classNames="list__item" timeout={800}>
                <li className={styles.contactList} key={contact.id}>
                  {contact.name}: {contact.number}
                  <button
                    className={styles.contactBtn}
                    onClick={deleteContact}
                    type="button"
                  >
                    Delete
                  </button>
                </li>
              </CSSTransition>
            )
          )
        )}
      </TransitionGroup>
    </ul>
  );
};

export default ContactList;
